import React, { useContext, useState, useEffect, createContext } from "react";
import { useNavigate } from "react-router-dom";
export const Headercontext = createContext();

const HeaderData = ({ children }) => {
  const navigate = useNavigate();
  const [sortedproducts, setsortedProducts] = useState([]);
  const [sortedproductsduplicate, setsortedProductsduplicate] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState("");
  const [selectedPrimaryCategory, setSelectedPrimaryCategory] = useState("");
  const [PrimaryCategoriesData, setPrimaryCategoriesData] = useState([]);
  const [selectedSubcategory, setselectedSubcategory] = useState("");
  const [toggle, setToggle] = useState(false);
  const [searchterm, setsearchterm] = useState("");
  const [showFilter, setShowfilters] = useState(false);
  const [showSort, setShowsort] = useState(false);
  const [searchedProducts, setsearchedProducts] = useState(sortedproducts);
  const [brandData, setBrandData] = useState([]);
  const [subcategoriesData, setSubCategoriesData] = useState([]);
  const [lenghtofsearch, setlengthofseacrh] = useState(0);
  const [favourite, setFavourite] = useState([]);
  const [favoriteArray, setFavoruitearray] = useState([]);
  const [imageLoad, setImageLoad] = useState(true);
  const [page, setPage] = useState(1);

  const [allData, setAlldata] = useState([]);
  const [scrollWidth, setScrollWidth] = useState(
    window.innerWidth - document.body.clientWidth
  );
  const [userID, setUserid] = useState("");

  // states for displaying popups
  const [login, setLogin] = useState(false);
  const [signup, setSignup] = useState(false);
  const [forgetPassword, setForgetPassword] = useState(false);
  const [confirmUser, setConfirmUser] = useState(false);

  // react toast notification
  const [toastmessage, setToastMessage] = useState({
    display: false,
    message: "",
  });

  // loader for social icons
  const [googleLoader, setGoogleloader] = useState(false);
  const [facebookLoader, setFacebookloader] = useState(false);

  const [token, setToken] = useState(null);
  const [userDetails, setUserDetails] = useState({});

  const [active, setActive] = useState({
    value: "unattended",
    content: "Sort by Price",
  });

  const categories = [
    { value: "lowestprice", content: "Sort by Price (lowest)" },
    { value: "highestprice", content: "Sort by Price (highest)" },
  ];

  // sorting according to lowest/highest, categories, brands, subcatgories

  // add/remove  products in favorites

  // generates a random id
  function generateId() {
    return (
      Math.random().toString(36).substring(2) +
      new Date().getTime().toString(36)
    );
  }

  // hide/show scroll from body when any popup appears/disappear.

  //parse token and get user details.

  // to check user is loged in or not and have id or not.

  // load all products according to brand and store them in state (alldata).

  // function to set products on first load according to brand at zero index and their category.

  return (
    <Headercontext.Provider
      value={{
        token,
        setToken,
        userDetails,
        setUserDetails,
        login,
        setLogin,
        signup,
        setSignup,
        forgetPassword,
        setForgetPassword,
        toastmessage,
        setToastMessage,
        googleLoader,
        setGoogleloader,
        facebookLoader,
        setFacebookloader,
        confirmUser,
        setConfirmUser,
        userID,
        setUserid,
      }}
    >
      {children}
    </Headercontext.Provider>
  );
};

export default HeaderData;
