import React, { useState, useEffect, useContext } from "react";
import { CognitoUser } from "amazon-cognito-identity-js";
import { Headercontext } from "../Headerdata/HeaderData";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Layout from "../layout";
import Innerlayout from "../layout/Innerlayout";
import Inputbox from "../common/Inputbox";
import Button from "../common/Button";
import { Link, Navigate, useNavigate } from "react-router-dom";
import UserPool from "./UserPool";

const ForgotPassword = () => {
  const navigate = useNavigate();

  const { setLogin, setForgetPassword, setToastMessage } =
    useContext(Headercontext);

  const [email, setEmail] = useState("");
  const [newPassword, setNewpassword] = useState("");
  const [confirmPassword, setConfirmpassword] = useState("");
  const [isVerifyCode, setIsverifycode] = useState(false);
  const [verificationCode, setVerificationcode] = useState("");
  const [passwordIcon, setPasswordIcon] = useState(true);
  const [passwordIcon2, setPasswordIcon2] = useState(true);
  const [loader, setLoader] = useState(false);

  const [error, setError] = useState({
    isError: false,
    message: "",
  });

  useEffect(() => {
    setError({
      isError: false,
      message: "",
    });
  }, [email, newPassword, confirmPassword, verificationCode]);

  const onSubmit = (e) => {
    e.preventDefault();
    setLoader(true);
    const user = new CognitoUser({
      Username: email,
      Pool: UserPool,
    });
    user.forgotPassword({
      onSuccess: function (result) {
        console.log("call result: " + result);
        setLoader(false);
      },
      onFailure: function (err) {
        console.log(err);
        setError({
          isError: true,
          message: err.message || JSON.stringify(err),
        });
        setLoader(false);
      },
      inputVerificationCode: (data) => {
        console.log("Input code:", data);
        setIsverifycode(true);
        setLoader(false);
      },
    });
  };

  return (
    <Layout>
      <Innerlayout
        heading={"Forgot Password"}
        subheading="An email with a link will be sent to your email address to change password."
        onSubmit={(e) => onSubmit(e)}
      >
        <Inputbox
          type="email"
          placeholder="Enter your email"
          required
          heading="Email*"
          handlechange={(e) => {
            setEmail(e.target.value);
          }}
        />
        <Button actiontype={"Forgot Password"} type="submit" />
        <div>
          <p className="simpletext">Have you remembered your password</p>
          <Link to="/signin">
            <p className="forgot-password">Sign In</p>
          </Link>
        </div>
      </Innerlayout>
      <Innerlayout heading={"Set Password"} onSubmit={(e)=>{onSubmit(e)}}>
        <Inputbox
          type="password"
          placeholder="********"
          required
          heading="Password"
        />
        <Inputbox
          type="password"
          placeholder="********"
          required
          heading="Repeat Password"
        />
        <Button
          actiontype={"Set Password"}
          action={() => {
            console.log("scdcscsdcs");
          }}
        />
      </Innerlayout>
    </Layout>
  );
};

export default ForgotPassword;
