import { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Layout from "../components/layout/index";
import Innerlayout from "../components/layout/Innerlayout";
// import Header from "../components/Headerdata/HeaderData";
import { Headercontext } from "../components/Headerdata/HeaderData";

const Home = () => {
  const navigate = useNavigate();

  const { userDetails, token, setLogin, setToken, setToastMessage } =
    useContext(Headercontext);
  const [logoutState, setLogoutState] = useState(false);

  function logOut() {
    localStorage.clear();
    setToken(null);
    setToastMessage({
      display: true,
      message: "Logged Out Successfully",
    });
    setLogoutState(false);
  }
  return (
    <Layout>
      <Innerlayout heading={"User Data"}>
        {token ? (
          <>
            <div className="flex justify-center gap-3 items-center relative ">
              */}

              <>

                  <div className="flex flex-col justify-center items-center absolute gap-8 right-0  z-[1000] md:-right-4 mt-60 bg-[#f7f7f7] w-56 text-gray-600 px-4 pt-12 pb-4 rounded-lg  shadow-2xl  md:max-w-[300px]">
                    <div className="flex flex-col gap-2 items-center justify-center">
                      {userDetails.picture && (
                        <img
                          src={userDetails.picture}
                          className="w-[50px] rounded-full color-white"
                        />
                      )}
                      <p className="font-bold text-sm whitespace-nowrap">
                        {userDetails.username}
                      </p>
                      <p className=" text-xs whitespace-nowrap ">
                        {userDetails.userEmail}
                      </p>
                    </div>
                    <div
                      className="flex gap-2 justify-center items-center cursor-pointer"
                      onClick={logOut}
                    >
                      {/* <img
                        src="../assets/icon/logout.png"
                        className="w-[15px]"
                      /> */}
                      <p className="text-xs font-medium">Log out</p>
                    </div>
                  </div>
              </>
            </div>
          </>
        ) : (
          <Link to="/signin">
            <p className="forgot-password">Sign In</p>
          </Link>
        )}
      </Innerlayout>
    </Layout>
  );
};

export default Home;
