import React, { useState, useEffect, useContext } from "react";
import { Headercontext } from "../contexts/Headerdata/HeaderData";
import SocialButton from "./SocialButton";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Signup = () => {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [userConfirm, setUserConfirm] = useState(false);
  const [confirmCode, setConfirmCode] = useState("");
  const [passwordIcon, setPasswordIcon] = useState(true);
  const [loader, setLoader]= useState(false);
  const [error, setError] = useState({
    isError: false,
    message: "",
  });

  var AmazonCognitoIdentity = require("amazon-cognito-identity-js");

useEffect(() => {

}, [email,password,name,confirmCode,])


  const poolData = {
    UserPoolId: "ap-northeast-1_ub6BKbCOS",
    ClientId: "40l4r3ri3b721utkclttqju4j8",
  };

  var userPool = new AmazonCognitoIdentity.CognitoUserPool(poolData);

  var attributeList = [];

  var dataEmail = {
    Name: "email",
    Value: email,
  };

  var dataName = {
    Name: "name",
    Value: name,
  };

  var attributeEmail = new AmazonCognitoIdentity.CognitoUserAttribute(
    dataEmail
  );

  var attributeName = new AmazonCognitoIdentity.CognitoUserAttribute(
    dataName
  );

  attributeList.push(attributeEmail);
  attributeList.push(attributeName)

  const onSubmit = (e) => {
    e.preventDefault();
    setLoader(true)
    if (!userConfirm) {
      userPool.signUp(
        email,
        password,
        attributeList,
        null,
        function (err, result) {
          if (err) {
            // toast(err.message || JSON.stringify(err));
            setLoader(false)
            setError({
              isError: true,
              message: err.message || JSON.stringify(err),
            });
            return;
          }
          setLoader(false)
          setUserConfirm(true);
          console.log(result,'resultsss')
          setError({
            isError: false,
            message: "",
          });
        }
      );
    } else if (userConfirm) {
      let userData = {
        Username: email,
        Pool: userPool,
      };
      let cognitoUser = new AmazonCognitoIdentity.CognitoUser(userData);
      cognitoUser.confirmRegistration(
        confirmCode,
        true,
        function (err, result,data) {
          if (err) {
            // toast(err.message || JSON.stringify(err));
            setLoader(false)
            setError({
              isError: true,
              message: err.message || JSON.stringify(err),
            });
            return;
          }
          setLoader(false);
          setSignup(false);
          setLogin(true);
          setToastMessage({
            display: true,
            message: "Registered successfully"})
        }
      );
    }
  };




  return (
    <div className=" flex items-center justify-center min-h-[100vh] bg-black/80">
      <div className="flex flex-col relative  items-center gap-2 min-h-[90vh] max-h-[90vh] w-full p-12 overflow-y-auto   my-4 max-w-[500px] bg-white  shadow-md rounded-md mx-3 md:mx-8" onClick={(e)=>{e.stopPropagation()}}>
        <div>
        <img src="../assets/icon/close.png" alt="" className=" absolute top-8 right-8 w-[15px]  cursor-pointer"
            onClick={() => {
              setSignup(false);
            }}/>
        </div>
        <div className="max-w-[400px] w-full px-4  flex justify-between items-center">
          <div>
            <p className="font-semibold text-2xl">Create an account</p>
          </div>
        </div>
        <form
          action=""
          className="flex flex-col gap-4 w-full justify-center items-center px-4 max-w-[400px]"
          onSubmit={(e) => onSubmit(e)}
        >
          <div className="max-w-[500px] w-full">
            <label className="text-xs font-medium leading-none text-gray-800">
              Full Name
            </label>
            <input
              onChange={(e) => {
                setName(e.target.value);
              }}
              id="name"
              aria-labelledby="name"
              required
              type="text"
              placeholder="Enter your full name"
              disabled={userConfirm ? true : false}
              className={`bg-gray-200 border rounded  text-xs font-medium leading-none text-gray-800 py-3 w-full pl-3 mt-2 `}
            />
          </div>
          <div className="max-w-[500px] w-full">
            <label className="text-xs font-medium leading-none text-gray-800">
              Email
            </label>
            <input
              onChange={(e) => setEmail(e.target.value)}
              id="email"
              aria-labelledby="email"
              type="email"
              placeholder="Enter your Email"
              disabled={userConfirm ? true : false}
              className={`bg-gray-200 border rounded  text-xs font-medium leading-none text-gray-800 py-3 w-full pl-3 mt-2 `}
            />
          </div>
          <div className="mt-2  w-full max-w-[500px]">
            <label
              htmlFor="pass"
              className="text-xs font-medium leading-none text-gray-800"
            >
              Password
            </label>
            <div className="relative flex items-center justify-center">
              <input
                onChange={(e) => setPassword(e.target.value)}
                id="password"
                type={passwordIcon ? "password" : "text"}
                required
                placeholder="Create Password"
                disabled={userConfirm ? true : false}
                className={`bg-gray-200 border rounded  text-xs font-medium leading-none text-gray-800 py-3 w-full pl-3 mt-2 `}
              />

              <div
                className="absolute right-0 mt-2 mr-3 cursor-pointer"
                onClick={() => setPasswordIcon(!passwordIcon)}
              >
                {passwordIcon ? (
                  <img
                    src="../assets/icon/show.png"
                    alt=""
                    className="opacity-80"
                    width={20}
                  />
                ) : (
                  <img
                    src="../assets/icon/hide.png"
                    alt=""
                    className="opacity-80"
                    width={20}
                  />
                )}
              </div>
            </div>
          </div>

          {userConfirm && (
            <div className="max-w-[500px] w-full">
              <label className="text-xs font-medium leading-none text-gray-800">
              Please check your email for a verification code
              </label>
              <input
                onChange={(e) => setConfirmCode(e.target.value)}
                id="code"
                aria-labelledby="code"
                placeholder="Enter Code"
                type="text"
                required
                className={`bg-gray-200 border rounded  text-xs font-medium leading-none text-gray-800 py-3 w-full pl-3 mt-2 `}
              />
            </div>
          )}
          {error.isError && (
            <div className="mt-2 w-full relative max-w-[500px] rounded py-4 px-2 bg-[#fae6e5] transition-all ease-in-out duration-1500 ">
              <div className="flex gap-4 justify-center items-center">
                <img
                  src="../assets/icon/error.svg"
                  alt=""
                  className="opacity-80"
                  width={20}
                />
                <p className="text-xs font-light ">{error.message}</p>
              </div>
            </div>
          )}
          <div className="mt-4 w-full max-w-[400px]">
            {!userConfirm ? (
              <button
                role="button"
                id="login-btn"
                className="focus:ring-2 disabled:bg-slate-500 focus:ring-offset-2 focus:ring-black text-sm font-semibold leading-none text-white focus:outline-none bg-black border rounded hover:bg-black/80 py-4 w-full h-[50px]"
              >
               {!loader ?   "Sign up" : <img src="../assets/loaders/loader_white.svg" alt="" className="w-[20px] mx-auto"/>} 
              </button>
            ) : (
              <button
                role="button"
                id="login-btn"
                className="focus:ring-2 disabled:bg-slate-500 focus:ring-offset-2 focus:ring-black text-sm font-semibold leading-none text-white focus:outline-none bg-black border rounded hover:bg-black/80 py-4 w-full"
              >
                 {!loader ?   "Confirm" : <img src="../assets/loaders/loader_white.svg" alt="" className="w-[20px] mx-auto"/>} 
              </button>
            )}
          </div>


        </form>
        <div className="w-full flex items-center justify-between py-2 max-w-[400px]">
          <hr className="w-full bg-gray-400" />
          <p className="text-base font-medium leading-4 px-2.5 text-gray-400">
            OR
          </p>
          <hr className="w-full bg-gray-400  " />
        </div>
        <SocialButton />
        <div>
          <span className=" text-xs font-medium px-[4px]">
            Already on SaleBag?
          </span>

          <span
            className=" text-sm font-semibold text-[#3e92f2] hover:underline cursor-pointer"
            onClick={() => {
              setSignup(false);
              setLogin(true);
            }}
          >
            Login
          </span>
        </div>
      </div>
      <ToastContainer />
