import React, { useContext, useState, useEffect, createContext } from "react";
import { useNavigate } from "react-router-dom";
export const Headercontext = createContext();

const HeaderData = ({ children }) => {
  const navigate = useNavigate();
  const [primaryCategories, setPrimaryCategories] = useState([]);
  const [subCategory, setSubCategory] = useState([]);
  const [allBrands, setAllbrands] = useState();
  const [sortedproducts, setsortedProducts] = useState([]);
  const [sortedproductsduplicate, setsortedProductsduplicate] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState("");
  const [selectedPrimaryCategory, setSelectedPrimaryCategory] = useState("");
  const [PrimaryCategoriesData, setPrimaryCategoriesData] = useState([]);
  const [selectedSubcategory, setselectedSubcategory] = useState("");
  const [toggle, setToggle] = useState(false);
  const [searchterm, setsearchterm] = useState("");
  const [showFilter, setShowfilters] = useState(false);
  const [showSort, setShowsort] = useState(false);
  const [searchedProducts, setsearchedProducts] = useState(sortedproducts);
  const [brandData, setBrandData] = useState([]);
  const [subcategoriesData, setSubCategoriesData] = useState([]);
  const [lenghtofsearch, setlengthofseacrh] = useState(0);
  const [favourite, setFavourite] = useState([]);
  const [favoriteArray, setFavoruitearray] = useState([]);
  const [imageLoad, setImageLoad] = useState(true);
  const [page, setPage] = useState(1);

  const [allData, setAlldata] = useState([]);
  const [scrollWidth, setScrollWidth] = useState(
    window.innerWidth - document.body.clientWidth
  );
  const [userID, setUserid] = useState("");

  // states for displaying popups
  const [login, setLogin] = useState(false);
  const [signup, setSignup] = useState(false);
  const [forgetPassword, setForgetPassword] = useState(false);
  const [confirmUser, setConfirmUser] = useState(false);


  // react toast notification
  const [toastmessage, setToastMessage] = useState({
    display: false,
    message: "",
  });


  // loader for social icons
  const [googleLoader, setGoogleloader] = useState(false);
  const [facebookLoader, setFacebookloader] = useState(false);

  const [token, setToken] = useState(null);
  const [userDetails, setUserDetails] = useState({});

  const [active, setActive] = useState({
    value: "unattended",
    content: "Sort by Price",
  });


  const categories = [
    { value: "lowestprice", content: "Sort by Price (lowest)" },
    { value: "highestprice", content: "Sort by Price (highest)" },
  ];


  // sorting according to lowest/highest, categories, brands, subcatgories


  // add/remove  products in favorites


  // generates a random id
  function generateId() {
    return (
      Math.random().toString(36).substring(2) +
      new Date().getTime().toString(36)
    );
  }

  // hide/show scroll from body when any popup appears/disappear.

  //parse token and get user details.
  function parseJwt() {
    var authData = {
      UserPoolId: "ap-northeast-1_5bRhyo0Eq",
      ClientId: "6j2dqmof4oiis8rg3kp1sh7hlb",
      RedirectUriSignIn:
        "https://salebag-mohsinidrees2817-gmailcom.vercel.app/",
      RedirectUriSignOut:
        "https://salebag-mohsinidrees2817-gmailcom.vercel.app/",
      AppWebDomain: "salebag2.auth.ap-northeast-1.amazoncognito.com",
      TokenScopesArray: ["email"],
    };
    var auth = new CognitoAuth(authData);
    auth.userhandler = {
      onSuccess: function (result) {
        let token = result.idToken.jwtToken;
        var base64Url = token.split(".")[1];
        var base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
        var jsonPayload = decodeURIComponent(
          window
            .atob(base64)
            .split("")
            .map(function (c) {
              return "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2);
            })
            .join("")
        );
        let userdata = JSON.parse(jsonPayload);
        console.log(userdata);
        let userData = {
          username: userdata?.name,
          userEmail: userdata?.email,
          picture:
            userdata?.identities[0].providerName == "Facebook"
              ? `https://graph.facebook.com/me/picture?access_token=${userdata.picture}`
              : userdata.picture,
          userId: userdata?.identities[0].userId,
          expTime: userdata?.exp,
          provider: userdata?.identities[0].providerName,
        };
        let user_ID = userdata?.identities[0].userId;

        setUserDetails(userData);
        localStorage.setItem("user_details", JSON.stringify(userData));
        setToken(token);
        localStorage.setItem("token", token);
        setUserid(user_ID);
        localStorage.setItem("user_ID", user_ID);
        navigate("/");
      },
      onFailure: function (err) {
        console.log(err, "errrr");
      },
    };
    var curUrl = window.location.href;
    auth.parseCognitoWebResponse(curUrl);
  }

  // to check user is loged in or not and have id or not.
  const IsloggedIn = () => {
    const token = localStorage.getItem("token");
    if (token) {
      const userDetails = JSON.parse(localStorage.getItem("user_details"));
      const ID = localStorage.getItem("user_ID");
      token && setToken(token);
      userDetails && setUserDetails(userDetails);
      ID && setUserid(ID);

      const favouritesID = JSON.parse(localStorage.getItem("FavouritesID"));
      if (favouritesID) {
        const favourites = JSON.parse(localStorage.getItem("Favourites"));
        favourites && setFavourite(favourites);
        favoriteArray && setFavoruitearray(favouritesID);
      }
      console.log("User already logged in");
    } else {
      const url = window.location;
      const href = url.href;
      const strs = href.split("#access_token=");
      const access_token = strs.at(1);
      if (access_token !== undefined) {
        parseJwt();
        console.log("recieved token in url user just logged in");
      } else {
        console.log("user not logged in");
        const id = localStorage.getItem("user_ID");
        if (id) {
          console.log("Already Have id", id);
          id && setUserid(id);

          const favouritesID = JSON.parse(localStorage.getItem("FavouritesID"));
          if (favouritesID) {
            const favourites = JSON.parse(localStorage.getItem("Favourites"));
            favourites && setFavourite(favourites);
            favoriteArray && setFavoruitearray(favouritesID);
          }

        } else {
          let ID = generateId();
          setUserid(ID);
          localStorage.setItem("user_ID", ID);
          console.log(
            "Generated an id for user and saved in local storage",
            ID
          );
        }
      }
    }
  };


  // load all products according to brand and store them in state (alldata).
  async function loadAllproducts() {
    let dataarray = [];
    data.map(async (brand, index) => {
      let importeddata = await import(`../../../${brand.data}`);
      let brands = {
        name: brand.name,
        data: Object.values(importeddata),
      };
      dataarray.push(brands);
    });
    setAlldata(dataarray);
  }

  // function to set products on first load according to brand at zero index and their category.
  const showFirstProducts = async () => {
    let filteredItems = allBrands[0];
    let importeddata = await import(`../../../${filteredItems.data}`);
    let data = Object.values(importeddata);
    setSelectedBrand(filteredItems.name);
    const primaryCategories = filteredItems.categories.map(
      (products, index) => {
        return products;
      }
    );
    setPrimaryCategoriesData(data);
    setPrimaryCategories(primaryCategories);
    setSelectedPrimaryCategory(primaryCategories[0].name);
    let Items = data;
    let filteredProducts = Items.filter((product, index) => {
      return product.category?.includes(primaryCategories[0].name);
    });
    setsortedProducts(filteredProducts);
    setsortedProductsduplicate(filteredProducts);
    setSubCategoriesData(filteredProducts);
    setSubCategory(filteredItems.categories[0].subCategories);
    setBrandData(allBrands[0]);
  };

  useEffect(() => {
    loadAllproducts();
    showFirstProducts();
    IsloggedIn();
  }, []);

  return (
    <Headercontext.Provider
      value={{
        sortedproducts,
        setsortedProducts,
        primaryCategories,
        setPrimaryCategories,
        subCategory,
        setSubCategory,
        allBrands,
        setAllbrands,
        selectedBrand,
        selectedPrimaryCategory,
        setSelectedBrand,
        setSelectedPrimaryCategory,
        PrimaryCategoriesData,
        setPrimaryCategoriesData,
        selectedSubcategory,
        setselectedSubcategory,
        toggle,
        setToggle,
        searchterm,
        setsearchterm,
        showFilter,
        setShowfilters,
        showSort,
        setShowsort,
        brandData,
        setBrandData,
        subcategoriesData,
        setSubCategoriesData,
        searchedProducts,
        setsearchedProducts,
        sorting,
        active,
        setActive,
        categories,
        lenghtofsearch,
        sortedproductsduplicate,
        setsortedProductsduplicate,
        favourite,
        setFavourite,
        imageLoad,
        setImageLoad,
        favoriteArray,
        setFavoruitearray,
        addToFavourite,
        token,
        setToken,
        userDetails,
        setUserDetails,
        scrollWidth,
        login,
        setLogin,
        signup,
        setSignup,
        forgetPassword,
        setForgetPassword,
        toastmessage,
        setToastMessage,
        googleLoader,
        setGoogleloader,
        facebookLoader,
        setFacebookloader,
        confirmUser,
        setConfirmUser,
        userID,
        setUserid,
        page,
        setPage,
      }}
    >
      {children}
    </Headercontext.Provider>
  );
};

export default HeaderData;
