import React, { useState, useEffect, useContext } from "react";
import {
  CognitoUser,
  AuthenticationDetails,
  CognitoUserPool,
  CognitoUserAttribute,
} from "amazon-cognito-identity-js";
import { Link, Navigate, redirect, useNavigate } from "react-router-dom";
import Layout from "../layout";
import Innerlayout from "../layout/Innerlayout";
import Inputbox from "../common/Inputbox";
import Button from "../common/Button";
import UserPool from "./UserPool";
import { Headercontext } from "../Headerdata/HeaderData";
import { createImageFromInitials } from "../ImageCreation/createImage";
import { getRandomColor } from "../ImageCreation/getrandomcolor";
import SocialButton from "./SocialButton";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Error from "../common/Error";
const Login = () => {
  const {
    setUserDetails,
    setToken,
    setLogin,
    setToastMessage,
    setConfirmUser,
    confirmUser,
    setUserid,
  } = useContext(Headercontext);
  const navigate = useNavigate();

  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [passwordIcon, setPasswordIcon] = useState(true);
  const [error, setError] = useState({
    isError: false,
    message: "",
  });
  const [loader, setLoader] = useState(false);
  const [confirmCode, setConfirmCode] = useState("");
  const [comfirmLoader, setConfirmLoader] = useState(false);

  useEffect(() => {
    setError({
      isError: false,
      message: "",
    });
  }, [email, password, confirmCode]);

  var AmazonCognitoIdentity = require("amazon-cognito-identity-js");
  const userPool = new CognitoUserPool({
    UserPoolId: "ap-northeast-1_ub6BKbCOS",
    ClientId: "40l4r3ri3b721utkclttqju4j8",
  });
  const onSubmit = (e) => {
    e.preventDefault();
    setLoader(true);

    const user = new CognitoUser({
      Username: email,
      Pool: UserPool,
    });

    const authDetails = new AuthenticationDetails({
      email: email,
      Password: password,
    });

    user.authenticateUser(authDetails, {
      onSuccess: (data) => {
        console.log(data);
        let userData = {
          username: data.idToken.payload.name,
          userEmail: data.idToken.payload.email,
          picture: createImageFromInitials(
            500,
            data.idToken.payload.name,
            getRandomColor()
          ),
          userId: data.accessToken.payload.client_id,
          expTime: data.accessToken.payload.exp,
          alldata: data,
        };
        setUserDetails(userData);
        setToken(data.accessToken.jwtToken);
        setUserid(data.accessToken.payload.client_id);
        localStorage.setItem("user_ID", data.accessToken.payload.client_id);
        localStorage.setItem("user_details", JSON.stringify(userData));
        localStorage.setItem("token", data.accessToken.jwtToken);
        setLoader(false);
        setLogin(false);
        setToastMessage({
          display: true,
          message: "Logged in successfully",
        });
        navigate("/");
      },
      onFailure: (err) => {
        // toast(err.message || JSON.stringify(err));
        if (err.message == "User is not confirmed.") {
          const confirmUser = new CognitoUser({
            Username: email,
            Pool: userPool,
          });
          confirmUser.resendConfirmationCode((err, result) => {
            if (err) {
              setError({
                isError: true,
                message: err.message || JSON.stringify(err),
              });
            } else {
              setToastMessage({
                display: true,
                message: "Confirmation code sent to email",
              });
              setConfirmUser(true);
              setLoader(false);
              // navigate("/verify");
            }
          });
        } else {
          setLoader(false);
          setError({
            isError: true,
            message: err.message || JSON.stringify(err),
          });
        }
      },
      newPasswordRequired: (data) => {
        // console.log("newPasswordRequired:", data);
      },
    });
  };
  return (
    <Layout>
      {confirmUser ? (
        <Innerlayout heading={"Sign In"} onSubmit={(e) => onSubmit(e)}>
          <div className="socialbuttons">
            <SocialButton />
          </div>
          <div className="divider">or login with email</div>

          <Inputbox
            type="email"
            placeholder="Enter your email"
            required
            heading="Email*"
            handlechange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <Inputbox
            type="password"
            placeholder="******"
            required
            heading="Password*"
            handlechange={(e) => {
              setPassword(e.target.value);
            }}
          />
          {error.isError && <Error errormessage={error.message} />}
          <Button actiontype={"Sign In"} />
          <Link to="/forgotpassword">
            <p className="forgot-password">Forgot Password?</p>
          </Link>
          <Link to="/signup">
            <p className="simpletext">
              Dont't have an account?{" "}
              <span className="forgot-password">Signup</span>
            </p>
          </Link>
        </Innerlayout>
      ) : (
        <Innerlayout
          heading={"Verify Email"}
          subheading="Enter verification code sent to your email to verify."
        >
          <Inputbox type="text" placeholder="000000" required />
          <Button
            actiontype={"verify"}
            action={() => {
              console.log("scdcscsdcs");
            }}
          />
        </Innerlayout>
      )}
    </Layout>
  );
};

export default Login;
